mapper.insert:
INSERT INTO SHOP_ITEM (
    ITEM_CODE
    , ITEM_NAME
    , ITEM_PRICE
    , CATE_CODE
    , ITEM_INTRO
) VALUES (
#{itemCode}
, #{itemName}
, #{itemPrice}
, #{cateCode}
, #{itemIntro}
)

mapper.select:
SELECT BUY_CODE , MEMBER_ID , BUY_PRICE , DATE_FORMAT(BUY_DATE, '%Y-%m-%d %h:%i') BUY_DATE FROM SHOP_BUY WHERE 1 = 1
AND ${searchType} LIKE CONCAT( '%', #{searchValue}, '%' )
AND DATE_FORMAT(BUY_DATE, '%Y-%m-%d') >= #{fromDate}
AND DATE_FORMAT(BUY_DATE, '%Y-%m-%d') <= #{toDate}
ORDER BY BUY_DATE DESC

mapper.update:
UPDATE SHOP_ITEM
SET
    CATE_CODE = #{cateCode}
    , ITEM_NAME = #{itemName}
    , ITEM_STOCK = #{itemStock}
    , ITEM_STATUS = #{itemStatus}
WHERE ITEM_CODE = #{itemCode}

mapper.select:
SELECT ATTACHED_FILE_NAME
FROM  ITEM_IMAGE
WHERE IMG_CODE = #{imgCode}
