#{itemCode}
, #{itemName}
, #{itemPrice}
, #{cateCode}
, #{itemIntro}
)

mapper.select:
SELECT * FROM SHOP_BUY
WHERE ${searchType} LIKE CONCAT ( '%', #{searchValue}, '%')
ORDER BY BUY_DATE DESC;
